    public int solve(ArrayList<Integer> A, ArrayList<Integer> B) {
        int n = A.size();
        int count = 0;

        HashMap<Integer, Map<Integer, Integer>> xMap = new HashMap<>();
        HashMap<Integer, Map<Integer, Integer>> yMap = new HashMap<>();


        for (int i = 0; i < n; i++) {
            Integer iX = A.get(i);
            Integer iY = B.get(i);

            for (int j = i + 1; j < n; j++) {
                Integer jX = A.get(j);
                Integer jY = B.get(j);


                if (iX.equals(jX)) {
                    doSomethinWithMap(yMap, iY, iX);
                    doSomethinWithMap(yMap, jY, jX);

                }
                if (iY.equals(jY)) {
                    doSomethinWithMap(xMap, iX, iY);
                    doSomethinWithMap(xMap, jX, jY);
                }
            }
        }

        for (int k = 0; k < n; k++) {
            Integer kX = A.get(k);
            Integer kY = B.get(k);

            if (xMap.containsKey(kX)) {
                Map<Integer, Integer> forbidenY = xMap.get(kX);


                for (Map.Entry<Integer, Integer> entry : forbidenY.entrySet()) {
                    if (!entry.getKey().equals(kY)) {
                        count += entry.getValue();
                    }
                }
            } else if (yMap.containsKey(kY)) {

                Map<Integer, Integer> forbidenX = yMap.get(kY);

                for (Map.Entry<Integer, Integer> entry : forbidenX.entrySet()) {
                    if (!entry.getKey().equals(kX)) {
                        count += entry.getValue();
                    }
                }
            }
        }

        return (int) (count % (Math.pow(10, 9) + 7));
    }

    private void doSomethinWithMap(HashMap<Integer, Map<Integer, Integer>> map, Integer allow, Integer forbid) {
        Map<Integer, Integer> listToForbidX = map.get(allow);

        if (listToForbidX == null) {
            listToForbidX = new HashMap<>();
        }

        Integer prevCount = listToForbidX.getOrDefault(forbid, 0);

        listToForbidX.put(forbid, prevCount + 1);
        map.put(allow, listToForbidX);
    }

    public int solveBrutForce(ArrayList<Integer> A, ArrayList<Integer> B) {
        int n = A.size();
        int count = 0;


        for (int i = 0; i < n; i++) {
            Integer xI = A.get(i);
            Integer yI = B.get(i);

            for (int j = i + 1; j < n; j++) {
                Integer xJ = A.get(j);
                Integer yJ = B.get(j);

                for (int k = j + 1; k < n; k++) {
                    Integer xK = A.get(k);
                    Integer yK = B.get(k);

                    if (xI.equals(xJ) || xI.equals(xK) || xJ.equals(xK)) {
                        if (yI.equals(yJ) || yI.equals(yK) || yJ.equals(yK)) {
                            count++;
                        }
                    }
                }
            }
        }

        return (int) (count % (Math.pow(10, 9) + 7));
    }
